import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';

import Card from '../components/card';
import FormGroup from '../components/form-group';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import axios from 'axios';
import { BASE_URL } from '../config/axios';
//import ListagemCategorias from './listagem-categorias';

function CadastroFuncionario() {
    const { idParam } = useParams();

    const navigate = useNavigate();

    const baseURL = `${BASE_URL}jsonfake/categorias`;

    const [id, setId] = useState('');
    const [nome, setNome] = useState('');
    const [descricao, setDescricao] = useState('');
    const [categoriaPai, setCategoriaPai] = useState('');

    const [dados, setDados] = useState([]);

    const [listaCategoriasPai, setListaCategoriasPai] = useState([]);

    function gerarCategoriasPaiOptions() {
        let listaCategoriasPaiOptions = (<option value={0}>Nenhuma</option>);
        for (let i = 0; i < listaCategoriasPai.length; i++) {
            let atual = listaCategoriasPai[i];
            if (categoriaPai === 0 || categoriaPai == null) continue;
            if (atual.id !== id) {
                listaCategoriasPaiOptions += (<option {...atual.id === categoriaPai.id ? "selected" : ""} value={atual.id}>{atual.name}</option>);
            }
        };
        console.log(listaCategoriasPaiOptions);
        return stringify(listaCategoriasPaiOptions);
    }

    listaCategoriasPai.map((x) => {
        return { value: x.id, label: x.nome };
    });

    function inicializar() {
        if (idParam == null) {
            setId('');
            setNome('');
            setDescricao('');
            setCategoriaPai(0);
        } else {
            setId(dados.id);
            setNome(dados.nome);
            setDescricao(dados.descricao);
            setCategoriaPai(dados.categoriaPai == null ? 0 : dados.categoriaPai);
        }
    }

    async function salvar() {
        let data = { id, nome, descricao, categoriaPai };
        data = JSON.stringify(data);
        if (idParam == null) {
            await axios
                .post(baseURL, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Categoria ${nome} cadastrado com sucesso!`);
                    navigate(`/listagem-categorias`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        } else {
            await axios
                .put(`${baseURL}/${idParam}`, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Categoria ${nome} alterada com sucesso!`);
                    navigate(`/listagem-categorias`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        }
    }

    async function buscar() {
        if (idParam != null) {
            await axios.get(`${baseURL}/${idParam}`).then((response) => {
                setDados(response.data);
            }).catch((a) => {
                console.log(a);
            });
            setId(dados.id);
            setNome(dados.nome);
            setDescricao(dados.descricao);
            setCategoriaPai(dados.categoriaPai);

            await axios.get(`${baseURL}`).then((response) => {
                setListaCategoriasPai(response.data);
            }).catch((a) => {
                console.log(a);
            });
        }
    }

    useEffect(() => {
        buscar(); // eslint-disable-next-line
    }, [id]);

    if (!dados) return null;

    return (
        <div className='container'>
            <Card title='Cadastro de Categoria'>
                <div className='row'>
                    <div className='col-lg-12'>
                        <div className='bs-component'>
                            <FormGroup label='Nome: *' htmlFor='inputNome'>
                                <input
                                    type='text'
                                    id='inputNome'
                                    value={nome}
                                    className='form-control'
                                    name='nome'
                                    onChange={(e) => setNome(e.target.value)}
                                />
                            </FormGroup>
                            <FormGroup label='Descricao: *' htmlFor='inputDescricao'>
                                <input
                                    type='text'
                                    id='inputDescricao'
                                    value={descricao}
                                    className='form-control'
                                    name='descricao'
                                    onChange={(e) => setDescricao(e.target.value)}
                                />
                            </FormGroup>
                            <FormGroup label='Categoria pai: ' htmlFor='inputCategoriaPai'>
                                <select
                                    //type='text'
                                    id='inputCategoriaPai'
                                    defaultValue={categoriaPai != null ? categoriaPai.id : 0}
                                    className='form-control'
                                    name='categoriaPai'
                                    onChange={(e) => setCategoriaPai(e.target.value)}>

                                    {gerarCategoriasPaiOptions()}
                                </select>

                            </FormGroup>
                            <Stack spacing={1} padding={1} direction='row'>
                                <button
                                    onClick={salvar}
                                    type='button'
                                    className='btn btn-success'
                                >
                                    Salvar
                                </button>
                                <button
                                    onClick={inicializar}
                                    type='button'
                                    className='btn btn-danger'
                                >
                                    Cancelar
                                </button>
                            </Stack>
                        </div>
                    </div>
                </div>
            </Card>
        </div>
    );
}

export default CadastroFuncionario;
