import React from 'react';

import Card from '../components/card';

// import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import { useNavigate } from 'react-router-dom';

import Stack from '@mui/material/Stack';
import { IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

// import axios from 'axios';
// import { BASE_URL } from '../config/axios';

// const baseURL = `${BASE_URL}jsonfake2/estoques`;

function adicionarItemCarrinho(item) {



}

function ListagemCarrinho() {
    //   const navigate = useNavigate();

    //   const cadastrar = () => {
    //     navigate(`/cadastro-estoques`);
    //   };

    //   const editar = (id) => {
    //     navigate(`/cadastro-estoques/${id}`);
    //   };


    const [itensCarrinho, setItensCarrinho] = React.useState([]);

    React.useEffect(() => {
        localStorage.setItem('itensCarrinho', JSON.stringify(itensCarrinho));
    }, [itensCarrinho]);

    React.useEffect(() => {
        const itensCarrinho = JSON.parse(localStorage.getItem('itensCarrinho'));
        if (itensCarrinho) {
            setItensCarrinho(itensCarrinho);
        }
    }, []);


    // async function excluir(id) {
    //     let data = JSON.stringify({ id });
    //     let url = `${baseURL}/${id}`;
    //     //console.log(url);
    //     await axios
    //         .delete(url, data, {
    //             headers: { 'Content-Type': 'application/json' },
    //         })
    //         .then(function (response) {
    //             mensagemSucesso(`Usuário excluído com sucesso!`);
    //             setDados(
    //                 dados.filter((dado) => {
    //                     return dado.id !== id;
    //                 })
    //             );
    //         })
    //         .catch(function (error) {
    //             mensagemErro(`Erro ao excluir o usuário`);
    //         });
    // }

    // React.useEffect(() => {
    //     axios.get(baseURL).then((response) => {
    //         setDados(response.data);
    //     });
    // }, []);



    if (!itensCarrinho) return null;
    return (
        <div className='container'>
            <Card title='Carrinho'>
                <div className='row'>
                    <div className='col-lg-12'>
                        <div className='bs-component'>
                            <button
                                type='button'
                                className='btn btn-warning'
                            // onClick={() => cadastrar()}
                            >
                                Esvaziar carrinho
                            </button>
                            <table className='table table-hover'>
                                <thead>
                                    <tr>

                                        <th scope='col'>Produto</th>
                                        <th scope='col'>Quantidade</th>
                                        <th scope='col'>Validade</th>
                                        <th scope='col'>Farmácia</th>
                                        <th scope='col'>Ações</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {itensCarrinho.map((dado) => (
                                        <tr key={dado.id}>
                                            <td>{dado.produto.nome}</td>
                                            <td>{dado.quantidade}</td>
                                            <td>{dado.dataValidade === "" ? "---" : dado.dataValidade}</td>
                                            <td>{dado.farmacia.nome}</td>
                                            <td>
                                                <Stack spacing={1} padding={0} direction='row'>
                                                    <IconButton
                                                        aria-label='edit'
                                                        style={{ color: "white" }}
                                                    // onClick={() => editar(dado.id)}
                                                    >
                                                        <EditIcon />
                                                    </IconButton>
                                                    <IconButton
                                                        aria-label='delete'
                                                        style={{ color: "red" }}
                                                    // onClick={() => excluir(dado.id)}
                                                    >
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Stack>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>{' '}
                        </div>
                    </div>
                </div>
            </Card>
        </div>
    );
}

export default ListagemCarrinho;
