import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';

import Card from '../components/card';
import FormGroup from '../components/form-group';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import axios from 'axios';
import { BASE_URL } from '../config/axios';

function CadastroFarmacias() {
	const { idParam } = useParams();

	const navigate = useNavigate();

	const baseURL = `${BASE_URL}jsonfake/farmacias`;

	const [id, setId] = useState('');
	const [nome, setNome] = useState('');
	const [email, setEmail] = useState('');
	const [cnpj, setCnpj] = useState('');
	const [telefone, setTelefone] = useState('');
	// endereço
	const [uf, setUf] = useState('');
	const [cidade, setCidade] = useState('');
	const [cep, setCep] = useState('');
	const [bairro, setBairro] = useState('');
	const [logradouro, setLogradouro] = useState('');
	const [numero, setNumero] = useState('');
	const [complemento, setComplemento] = useState('');

	const [dados, setDados] = useState([]);

	function inicializar() {
		if (idParam == null) {
			setId('');
			setNome('');
			setEmail('');
			setCnpj('');
			setTelefone('');
			// endereço
			setUf('');
			setCidade('');
			setCep('');
			setBairro('');
			setLogradouro('');
			setNumero('');
			setComplemento('');

		} else {
			setId(dados.id);
			setNome(dados.nome);
			setEmail(dados.email);
			setCnpj(dados.cnpj);
			setTelefone(dados.telefone);
			// endereço
			setUf(dados.uf);
			setCidade(dados.cep);
			setCep(dados.cep);
			setBairro(dados.bairro);
			setLogradouro(dados.logradouro);
			setNumero(dados.numero);
			setComplemento(dados.complemento);
		}
	}

	async function salvar() {
		let data = { id, nome, email, cnpj, telefone , uf, cidade, cep, bairro, logradouro, numero, complemento};
		data = JSON.stringify(data);
		if (idParam == null) {
			await axios
				.post(baseURL, data, {
					headers: { 'Content-Type': 'application/json' },
				})
				.then(function (response) { 
					mensagemSucesso(`Farmacia ${nome} cadastrada com sucesso!`);
					navigate(`/listagem-farmacias`);
				})
				.catch(function (error) {
					mensagemErro(error.response.data);
				});
		} else {
			await axios
				.put(`${baseURL}/${idParam}`, data, {
					headers: { 'Content-Type': 'application/json' },
				})
				.then(function (response) {
					mensagemSucesso(`Farmacia ${nome} alterada com sucesso!`);
					navigate(`/listagem-farmacias`);
				})
				.catch(function (error) {
					mensagemErro(error.response.data);
				});
		}
	}


	async function buscar() {
		if (idParam != null) {
			await axios.get(`${baseURL}/${idParam}`).then((response) => {
				setDados(response.data);
			}).catch((a) => {
				console.log(a);
			});
			setId(dados.id);
			setNome(dados.nome);
			setEmail(dados.email);
			setCnpj(dados.cnpj);
			setTelefone(dados.telefone);
			// endereço
			setUf(dados.uf);
			setCidade(dados.cep);
			setCep(dados.cep);
			setBairro(dados.bairro);
			setLogradouro(dados.logradouro);
			setNumero(dados.numero);
			setComplemento(dados.complemento);
		}
	}

	useEffect(() => {
		buscar(); // eslint-disable-next-line
	}, [id]);

	if (!dados) return null;

	return (
		<div className='container'>
			<Card title='Cadastro de Farmacia'>
				<div className='row'>
					<div className='col-lg-12'>
						<div className='bs-component'>
							<FormGroup label='Nome: *' htmlFor='inputNome'>
								<input
									type='text'
									id='inputNome'
									value={nome}
									className='form-control'
									name='nome'
									onChange={(e) => setNome(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='E-mail: *' htmlFor='inputEmail'>
								<input
									type='text'
									id='inputEmail'
									value={email}
									className='form-control'
									name='email'
									onChange={(e) => setEmail(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='CNPJ: *' htmlFor='inputCnpj'>
								<input
									type='text'
									//maxLength='11'
									id='inputCnpj'
									value={cnpj}
									className='form-control'
									name='cnpj'
									onChange={(e) => setCnpj(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Telefone: ' htmlFor='inputTelefone'>
								<input
									type='text'
									id='inputTelefone'
									value={telefone}
									className='form-control'
									name='telefone'
									onChange={(e) => setTelefone(e.target.value)}
								/>
							</FormGroup>

							<br></br><h2>Endereço:</h2>

							<FormGroup label='UF: ' htmlFor='inputUf'>
								<input
									type='text'
									id='inputUf'
									value={uf}
									className='form-control'
									name='uf'
									onChange={(e) => setUf(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Cidade: ' htmlFor='inputCidade'>
								<input
									type='text'
									id='inputCidade'
									value={cidade}
									className='form-control'
									name='cidade'
									onChange={(e) => setCidade(e.target.value)}
								/>
							</FormGroup>

							<Stack spacing={1} padding={1} direction='row'>
								<button
									onClick={salvar}
									type='button'
									className='btn btn-success'
								>
									Salvar
								</button>
								<button
									onClick={inicializar}
									type='button'
									className='btn btn-danger'
								>
									Cancelar
								</button>
							</Stack> 
						</div>
					</div>
				</div>
			</Card>
		</div>
	);
}

export default CadastroFarmacias;
