import React from 'react';

class CarrinhoPopup extends React.Component {

    dados = [];

    constructor(props) {
        super(props);
        this.dados = localStorage.getItem("carrinho_itens") ?? {

        };
      }

    render() {
        return (
            <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Produto</th>
                    <th scope='col'>Quantidade</th>
                    <th scope='col'>Validade</th>
                    <th scope='col'>Farmácia</th>
                    <th scope='col'>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.map((dado) => (
                    <tr key={dado.id}>
                      <td>{dado.produto.nome}</td>
                      <td>{dado.quantidade}</td>
                      <td>{dado.dataValidade === "" ? "---" : dado.dataValidade}</td>
                      <td>{dado.farmacia.nome}</td>
                      <td>
                        <Stack spacing={1} padding={0} direction='row'>
                          <IconButton
                            aria-label='edit'
                            style={{ color: "white"}}
                            onClick={() => editar(dado.id)}
                          >
                            <EditIcon /> 
                          </IconButton>
                          <IconButton
                            aria-label='delete'
                            style={{ color: "red"}}
                            onClick={() => excluir(dado.id)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Stack>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
        );
    }
}

export default CarrinhoPopup;
