import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';

import Card from '../components/card';
import FormGroup from '../components/form-group';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import axios from 'axios';
import { BASE_URL } from '../config/axios';

function toDate(dateStr = "") {
    if (dateStr === undefined) {
        return new Date();
    }
    return dateStr.split('-').reverse().join('-');
}

function getById(id, list) {
	for (let i = 0; i < list.length; i++) {
		if (list[i].id === id) {
			return list[i];
		}
	}
	return null;
}

function CadastroFuncionarios() {
	const { idParam } = useParams();

	const navigate = useNavigate();

	const baseURL = `${BASE_URL}jsonfake/funcionarios`;

	const [id, setId] = useState('');
	const [nome, setNome] = useState('');
	const [email, setEmail] = useState('');
	const [cpf, setCpf] = useState('');
	const [senha, setSenha] = useState('');
	const [senhaRepeticao, setSenhaRepeticao] = useState('');
	const [telefone, setTelefone] = useState('');
	const [cargo, setCargo] = useState('');
	const [dataAdmissao, setDataAdmissao] = useState('');
	const [salario, setSalario] = useState(0);
	const [expediente, setExpediente] = useState('');
	const [farmacia, setFarmacia] = useState('');

	// endereço
	const [uf, setUf] = useState('');
	const [cidade, setCidade] = useState('');
	const [cep, setCep] = useState('');
	const [bairro, setBairro] = useState('');
	const [logradouro, setLogradouro] = useState('');
	const [numero, setNumero] = useState('');
	const [complemento, setComplemento] = useState('');

	const [listaCargos, setListaCargos] = useState([]);
	const [listaFarmacias, setListaFarmacias] = useState([]);

	const [dados, setDados] = useState([]);

	function inicializar() {
		if (idParam == null) {
			setId('');
			setNome('');
			setEmail('');
			setCpf('');
			setSenha('');
			setSenhaRepeticao('');
			setTelefone('');
			setCargo(null);
			setDataAdmissao('');
			setSalario('');
			setExpediente('');
			setFarmacia(null);
			// endereço
			setUf('');
			setCidade('');
			setCep('');
			setBairro('');
			setLogradouro('');
			setNumero('');
			setComplemento('');
		} else {
			setId(dados.id);
			setNome(dados.nome);
			setEmail(dados.email);
			setCpf(dados.cpf);
			setSenha(dados.senha);
			setSenhaRepeticao(dados.senhaRepeticao);
			setTelefone(dados.telefone);
			setCargo(dados.cargo);
			setDataAdmissao(new Date(Date.parse(dados.dataAdmissao)));
			setSalario(dados.salario);
			setExpediente(dados.expediente);
			setFarmacia(dados.farmacia);
			// endereço
			setUf(dados.endereco.uf);
			setCidade(dados.endereco.cidade);
			setCep(dados.endereco.cep);
			setBairro(dados.endereco.bairro);
			setLogradouro(dados.endereco.logradouro);
			setNumero(dados.endereco.numero);
			setComplemento(dados.endereco.complemento);
		}
	}

	async function salvar() {
		let endereco = { uf, cidade, cep, bairro, logradouro, numero, complemento }
		let data = { id, nome, email, cpf, senha, telefone, endereco, cargo, dataAdmissao, salario, expediente, farmacia };
		data = JSON.stringify(data);
		if (idParam == null) {
			await axios
				.post(baseURL, data, {
					headers: { 'Content-Type': 'application/json' },
				})
				.then(function (response) {
					mensagemSucesso(`Usuário ${nome} cadastrado com sucesso!`);
					navigate(`/listagem-funcionarios`);
				})
				.catch(function (error) {
					mensagemErro(error.response.data);
				});
		} else {
			await axios
				.put(`${baseURL}/${idParam}`, data, {
					headers: { 'Content-Type': 'application/json' },
				})
				.then(function (response) {
					mensagemSucesso(`Usuário ${nome} alterado com sucesso!`);
					navigate(`/listagem-funcionarios`);
				})
				.catch(function (error) {
					mensagemErro(error.response.data);
				});
		}
	}


	async function buscar() {
		if (idParam != null) {
			await axios.get(`${baseURL}/${idParam}`).then((response) => {
				setDados(response.data);
			}).catch((a) => {
				console.log(a);
			});
			setId(dados.id);
			setNome(dados.nome);
			setEmail(dados.email);
			setCpf(dados.cpf);
			setSenha(dados.senha);
			setSenhaRepeticao(dados.senhaRepeticao);
			setTelefone(dados.telefone);
			setCargo(dados.cargo);
			setDataAdmissao(dados.dataAdmissao);
			setSalario(dados.salario);
			setExpediente(dados.expediente);
			setFarmacia(dados.farmacia);
			// endereço
			try {
				setUf(dados.endereco.uf);
				setCidade(dados.endereco.cidade);
				setCep(dados.endereco.cep);
				setBairro(dados.endereco.bairro);
				setLogradouro(dados.endereco.logradouro);
				setNumero(dados.endereco.numero);
				setComplemento(dados.endereco.complemento);
			}
			catch (e) {

			}
		}
		await axios.get(`${BASE_URL}/jsonfake2/cargos`).then((response) => {
            setListaCargos(response.data);
        }).catch((a) => {
            //console.log(a);
        });
	}

	useEffect(() => {
		buscar(); // eslint-disable-next-line
	}, [id]);

	if (!dados) return null;

	return (
		<div className='container'>
			<Card title='Cadastro de Funcionário'>
				<div className='row'>
					<div className='col-lg-12'>
						<div className='bs-component'>
							<FormGroup label='Nome: *' htmlFor='inputNome'>
								<input
									type='text'
									id='inputNome'
									value={nome}
									className='form-control'
									name='nome'
									onChange={(e) => setNome(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='E-mail: *' htmlFor='inputEmail'>
								<input
									type='text'
									id='inputEmail'
									value={email}
									className='form-control'
									name='email'
									onChange={(e) => setEmail(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='CPF: *' htmlFor='inputCpf'>
								<input
									type='text'
									maxLength='11'
									id='inputCpf'
									value={cpf}
									className='form-control'
									name='cpf'
									onChange={(e) => setCpf(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Senha: *' htmlFor='inputSenha'>
								<input
									type='password'
									id='inputSenha'
									value={senha}
									className='form-control'
									name='senha'
									onChange={(e) => setSenha(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Repita a Senha: *' htmlFor='inputRepitaSenha'>
								<input
									type='password'
									id='inputRepitaSenha'
									value={senhaRepeticao}
									className='form-control'
									name='senhaRepeticao'
									onChange={(e) => setSenhaRepeticao(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Telefone: ' htmlFor='inputTelefone'>
								<input
									type='text'
									id='inputTelefone'
									value={telefone}
									className='form-control'
									name='telefone'
									onChange={(e) => setTelefone(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Data de Admissão: ' htmlFor='inputDataAdmissao'>
								<input
									type='date'
									id='inputDataAdmissao'
									value={toDate(dataAdmissao)}
									className='form-control'
									name='dataAdmissao'
									onChange={(e) => setDataAdmissao(toDate(e.target.value))}
								/>
							</FormGroup>
							<FormGroup label='Cargo: ' htmlFor='inputCargo'>
                                <select
                                    type='text'
                                    id='inputCargo'
                                    value={cargo == null ? 0 : cargo.id}
                                    className='form-control'
                                    name='cargo'
                                    onChange={(e) => setCargo(getById(e.target.value, listaCargos))}
                                >
                                    {listaCargos.map((cat) => (
                                        <option value={cat.id} key={cat.id}>{`${cat.nome}`}</option>
                                    ))}
                                </select>
                            </FormGroup>
							<FormGroup label='Salário: *' htmlFor='inputSalario'>
								<input
									type='number'
									// min='0'
									// max='10000000'
									// step='0.01'
									id='inputSalario'
									value={salario}
									className='form-control'
									name='salario'
									onChange={(e) => setSalario(e.target.value)}
								/>
							</FormGroup>
							{/*
              //TODO: descobrir como fazer isso ser um dropdown pra escolher entre manhã, tarde, noite, madrugada ou sla oq mais
               */}
							<FormGroup label='Expediente: ' htmlFor='inputExpediente'>
								<select
									type='text'
									id='inputExpediente'
									value={expediente}
									className='form-control'
									name='expediente'
									onChange={(e) => setExpediente(e.target.value)}>
									<option value="manha" key="manha">Manhã</option>
									<option value="tarde" key="tarde">Tarde</option>
									<option value="noite" key="noite">Noite</option>
									<option value="madrugada" key="madrugada">Madrugada</option>
								</select>
							</FormGroup>

							<FormGroup label='Farmácia: ' htmlFor='inputFarmacia'>
								<select
									type='text'
									id='inputFarmacia'
									value={farmacia == null ? 0 : farmacia.id}
									className='form-control'
									name='farmacia'
									onChange={(e) => setFarmacia(getById(e.target.value, listaFarmacias))}>
									{listaFarmacias.map((cat) => (
                                        <option value={cat.id} key={cat.id}>{`${cat.nome}`}</option>
                                    ))}
								</select>

							</FormGroup>

							<br></br><h2>Endereço:</h2>

							<FormGroup label='UF: *' htmlFor='inputUf'>
								<input
									type='text'
									id='inputUf'
									value={uf}
									className='form-control'
									name='uf'
									onChange={(e) => setUf(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Cidade: *' htmlFor='inputCidade'>
								<input
									type='text'
									id='inputCidade'
									value={cidade}
									className='form-control'
									name='cidade'
									onChange={(e) => setCidade(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='CEP: *' htmlFor='inputCep'>
								<input
									type='text'
									id='inputCep'
									value={cep}
									className='form-control'
									name='cep'
									onChange={(e) => setCep(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Bairro: *' htmlFor='inputBairro'>
								<input
									type='text'
									id='inputBairro'
									value={bairro}
									className='form-control'
									name='bairro'
									onChange={(e) => setBairro(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Logradouro: *' htmlFor='inputLogradouro'>
								<input
									type='text'
									id='inputLogradouro'
									value={logradouro}
									className='form-control'
									name='logradouro'
									onChange={(e) => setLogradouro(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Número: *' htmlFor='inputNumero'>
								<input
									type='text'
									id='inputNumero'
									value={numero}
									className='form-control'
									name='numero'
									onChange={(e) => setNumero(e.target.value)}
								/>
							</FormGroup>
							<FormGroup label='Complemento: ' htmlFor='inputComplemento'>
								<input
									type='text'
									id='inputComplemento'
									value={complemento}
									className='form-control'
									name='complemento'
									onChange={(e) => setComplemento(e.target.value)}
								/>
							</FormGroup>
							{/* <FormGroup label='Farmácia: ' htmlFor='inputFarmacia'>
                <input
                  type=''
                  id='inputFarmacia'
                  value={farmacia}
                  className='form-control'
                  name='expediente'
                  onChange={(e) => setExpediente(e.target.value)}
                />
              </FormGroup> */}
							{/* <FormGroup>
                <input
                  className='form-check-input'
