import React from 'react';

import Card from '../components/card';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';
import { IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import Typography from '@mui/material/Typography';

import axios from 'axios';
import { BASE_URL } from '../config/axios';

const baseURL = `${BASE_URL}jsonfake2/estoques`;



function ListagemCarrinho() {

    const { idParam } = useParams();

    const navigate = useNavigate();

    //   const cadastrar = () => {
    //     navigate(`/cadastro-estoques`);
    //   };

    //   const editar = (id) => {
    //     navigate(`/cadastro-estoques/${id}`);
    //   };


    const [itensCarrinho, setItensCarrinho] = React.useState([]);

    React.useEffect(() => {
        const items = JSON.parse(localStorage.getItem('itensCarrinho'));
        if (items) {
            setItensCarrinho(items);
        }
    }, []);

    function calcularPrecoTotal() {
        var total = 0;

        for (var i = 0; i < itensCarrinho.length; i++) {
            total += itensCarrinho[i].produto.preco * itensCarrinho[i].quantidade;
        }

        return total;

    }

    async function salvarPedido() {
        // um pedido é composto por 
        // id, codigo, dataCriacao, status, valorTotal, tipoEntrega, dataEntrega, 
        // e endereco (que é o único objeto)

        // também está atrelado ao pedido os itensPedido, cada um sendo composto por
        // id, quantidade, precoUnitario, idPedido, idEstoque, idReceita

        // TODO: fazer as chamadas de api certo pra salvar o pedido

        let data = {};
        data = JSON.stringify(data);
        if (idParam == null) {
            await axios
                .post(baseURL, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Pedido cadastrado com sucesso!`);
                    navigate(`/listagem-pedidos`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        } else {
            await axios
                .put(`${baseURL}/${idParam}`, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Pedido cadastrado com sucesso!`);
                    navigate(`/listagem-pedidos`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        }
    }

    // React.useEffect(() => {
    //     localStorage.setItem('itensCarrinho', JSON.stringify(itensCarrinho));
    // }, [itensCarrinho]);


    // function adicionarItemCarrinho(item) {

    //     for (var i = 0; i < itensCarrinho.length; i++) {
    //         if (itensCarrinho[i].produto.id === item.id) {
    //             itensCarrinho[i].quantidade += 1;
    //             console.log(itensCarrinho);
    //             return;
    //         }
    //     }
    //     // console.log(itensCarrinho);
    //     var novoObj = {};
    //     novoObj.quantidade = 1;
    //     novoObj.produto = item;
    //     itensCarrinho.push(novoObj);
    //     console.log(itensCarrinho);
    //     // setItensCarrinho(itensCarrinho);

    // }


    async function excluir(id) {
        for (var i = 0; i < itensCarrinho.length; i++) {
            if (itensCarrinho[i].produto.id === id) {

                itensCarrinho.splice(i, 1); // 2nd parameter means remove one item only

            }
        }

    }

    // React.useEffect(() => {
    //     axios.get(baseURL).then((response) => {
    //         setDados(response.data);
    //     });
    // }, []);



    if (!itensCarrinho) return null;
    return (
        <div className='container'>
            <Card title='Carrinho'>
                <div className='row'>
                    <div className='col-lg-12'>
                        <div className='bs-component'>
                            <button
                                type='button'
                                className='btn btn-warning'
                                onClick={() => {
                                    setItensCarrinho([]);
                                    localStorage.setItem('itensCarrinho', "[]");
                                }
                                }
                            >
                                Esvaziar carrinho
                            </button>

                            <table className='table table-hover'>
                                <thead>
                                    <tr>

                                        <th scope='col'>Produto</th>
                                        <th scope='col'>Quantidade</th>
                                        <th scope='col'>Preço</th>
                                        <th scope='col'>Ações</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {itensCarrinho.map((dado) => (
                                        <tr key={dado.id}>
                                            <td>{dado.produto.nome}</td>
                                            <td>{dado.quantidade}</td>
                                            <td>{dado.produto.preco}</td>
                                            <td>
                                                <Stack spacing={1} padding={0} direction='row'>
                                                    {/* <IconButton
                                                        aria-label='edit'
                                                        style={{ color: "white" }}
                                                    // onClick={() => editar(dado.id)}
                                                    >
                                                        <EditIcon />
                                                    </IconButton> */}
                                                    <IconButton
                                                        aria-label='delete'
                                                        style={{ color: "red" }}
                                                        onClick={() => excluir(dado.id)}
                                                    >
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Stack>
                                            </td>
                                        </tr>
                                    ))}
                                    <tr key="-2">
                                        <td>---</td><td>---</td><td>---</td><td>---</td><td>---</td>
                                    </tr>
                                    <tr key="-1">
                                        <td>Total</td>
                                        <td>{0}</td> { /*calcularQuantidadeTotal()*/}
                                        <td>{calcularPrecoTotal()}</td>
                                        <td>
                                            <button
                                                type='button'
                                                className='btn btn-success'
                                                onClick={() => {
                                                    salvarPedido();
                                                    setItensCarrinho([]);
                                                    localStorage.setItem('itensCarrinho', "[]");
                                                }
                                                }
                                            >
                                                Confirmar pedido
                                            </button>
                                        </td>

                                    </tr>
                                </tbody>
                            </table>
                            <div style={{ "flex-wrap": "wrap", "align-items": "end" }}>
                                <Typography variant="body2" color="text.secondary" sx={{ fontSize: { xs: '0.9rem', sm: '1rem' }, color: "white" }}>
                                    Total: R$ {calcularPrecoTotal()}
                                </Typography>
                                <button
                                    type='button'
                                    className='btn btn-success'
                                    onClick={() => {
                                        salvarPedido();
                                        setItensCarrinho([]);
                                        localStorage.setItem('itensCarrinho', "[]");
                                    }
                                    }
                                >
                                    Confirmar pedido
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </Card >
        </div >
    );
}

export default ListagemCarrinho;
