import React from 'react';
import TextField from "@mui/material/TextField";
import SearchIcon from '@mui/icons-material/Search';
import InputAdornment from '@mui/material/InputAdornment';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

import imagemErro from '../img/imagem-erro.png'

const baseURL = `${BASE_URL}jsonfake/produtos`;



function LandingPage() {
    const [dados, setDados] = React.useState(null);



    React.useEffect(() => {
        axios.get(baseURL).then((response) => {
            setDados(response.data);
        });
    }, []);

    if (!dados) return null;

    return (
        <div className='container'>
            <h1>Catálogo</h1>
            <div className="search">
                <TextField
                    id="outlined-basic"
                    variant="outlined"
                    fullWidth
                    label="Pesquisar"
                    sx={{
                        backgroundColor: '#f0f0f0',
                        borderRadius: '8px'
                    }}
                    InputProps={{
                        style: {
                            color: '#333',
                            borderRadius: '8px'
                        },
                        endAdornment: (
                            <InputAdornment position="end">
                                <SearchIcon style={{ color: '#666' }} />
                            </InputAdornment>
                        )
                    }}
                />
            </div>

            <Grid container spacing={2} sx={{ marginTop: '20px' }}>
                {dados.map((produto) => (
                    <Grid item xs={4} key={produto.id}>
                        <Card sx={{ borderRadius: '8px', height: '100%', display: 'flex', flexDirection: 'column' }}>
                            <CardMedia
                                component="img"
                                height="140"
                                image={produto.imagem}
                                alt={produto.nome}
                            />
                            <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                                <div>
                                    <Typography gutterBottom variant="h5" component="div">
                                        {produto.nome}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        R$ {produto.preco.toFixed(2)}
                                    </Typography>
                                </div>
                                <Button
                                    variant="contained"
                                    fullWidth
                                    sx={{
                                        borderRadius: '8px',
                                        border: '2px solid #3f51b5',
                                        textTransform: 'none', // Desativa o CAPS LOCK
                                        marginTop: '10px', // Espaçamento opcional
                                    }}
                                    onClick={() => console.log(`Produto ${produto.id} adicionado ao carrinho`)}
                                >
                                    Adicionar ao Carrinho
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </div>
    );
}

export default LandingPage;
