import React from 'react';
import Stack from '@mui/material/Stack';
import Card from '../components/card';
import FormGroup from '../components/form-group';
import { mensagemSucesso } from '../components/toastr';

class Login extends React.Component {
  state = {
    login: '',
    senha: '',
  };

  logar = () => {
    mensagemSucesso(`Usuário ${this.state.login} logado com sucesso!`);
  };

  cancelar = () => {
    this.setState({
      login: '',
      senha: '',
    });
  };

  render() {
    return (
      <div 
        className='container' 
        style={{ 
          display: 'flex', 
          justifyContent: 'center', // Centraliza horizontalmente
          alignItems: 'center', // Centraliza verticalmente (opcional)
          height: '100vh', // Ocupa 100% da altura da tela
        }}
      >
        <div style={{ width: '100%', maxWidth: '400px' }}> {/* Largura máxima do card */}
          <Card title='Acesso'>
            <div className='row'>
              <div className='bs-component'>
                <FormGroup label='Login: *' htmlFor='inputLogin'>
                  <input
                    type='text'
                    id='inputLogin'
                    value={this.state.login}
                    className='form-control'
                    name='login'
                    onChange={(e) => this.setState({ login: e.target.value })}
                  />
                </FormGroup>
                <FormGroup label='Senha: *' htmlFor='inputSenha'>
                  <input
                    type='password'
                    id='inputSenha'
                    value={this.state.senha}
                    className='form-control'
                    name='senha'
                    onChange={(e) => this.setState({ senha: e.target.value })}
                  />
                </FormGroup>
                <Stack spacing={1} padding={1} direction='row'>
                  <button
                    onClick={this.logar}
                    type='button'
                    className='btn btn-success'
                  >
                    Entrar
                  </button>
                  <button
                    onClick={this.cancelar}
                    type='button'
                    className='btn btn-danger'
                  >
                    Cancelar
                  </button>
                  <div>Não possui conta?</div>
                  <button
                    onClick={() => console.log("ainda não fizemos o redirecionamento")}
                    type='button'
                    className='btn btn-'
                  >
                    Cadastre-se
                  </button>
                </Stack>
              </div>
            </div>
          </Card>
        </div>
      </div>
    );
  }
}

export default Login;
