import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';

import Card from '../components/card';
import FormGroup from '../components/form-group';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import axios from 'axios';
import { BASE_URL } from '../config/axios';
//import ListagemCategorias from './listagem-categorias';

function CadastroPedidos() {
    const { idParam } = useParams();

    const navigate = useNavigate();

    const baseURL = `${BASE_URL}jsonfake2/pedidos`;

    const [id, setId] = useState('');
    const [dataCriacao, setDataCriacao] = useState('');
    const [status, setStatus] = useState('');
    const [valorTotal, setValorTotal] = useState('');
    const [tipoEntrega, setTipoEntrega] = useState('');
    const [endereco, setEndereco] = useState('');

    const [dados, setDados] = useState([]);

    const [listaEnderecos, setListaEnderecos] = useState([]);


    function inicializar() {
        if (idParam == null) {
            setId('');
            setDataCriacao('');
            setStatus('');
            setValorTotal(0);
            setTipoEntrega('');
            setEndereco(null);
        } else {
            setId(dados.id);
            setDataCriacao(dados.dataCriacao);
            setStatus(dados.status);
            setValorTotal(dados.valorTotal);
            setTipoEntrega(dados.tipoEntrega);
            setEndereco(dados.endereco);
        }
    }

    async function salvar() {
        let data = { id, nome, descricao, categoriaPai };
        data = JSON.stringify(data);
        if (idParam == null) {
            await axios
                .post(baseURL, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Categoria ${nome} cadastrada com sucesso!`);
                    navigate(`/listagem-categorias`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        } else {
            await axios
                .put(`${baseURL}/${idParam}`, data, {
                    headers: { 'Content-Type': 'application/json' },
                })
                .then(function (response) {
                    mensagemSucesso(`Categoria ${nome} alterada com sucesso!`);
                    navigate(`/listagem-categorias`);
                })
                .catch(function (error) {
                    mensagemErro(error.response.data);
                });
        }
    }

    async function buscar() {
        if (idParam != null) {
            await axios.get(`${baseURL}/${idParam}`).then((response) => {
                setDados(response.data);
            }).catch((a) => {
                console.log(a);
            });
            setId(dados.id);
            setDataCriacao(dados.dataCriacao);
            setStatus(dados.status);
            setValorTotal(dados.valorTotal);
            setTipoEntrega(dados.tipoEntrega);
            setEndereco(dados.endereco);


        }
        await axios.get(`${BASE_URL}/jsonfake`).then((response) => {
            setListaEnderecos(response.data);
        }).catch((a) => {
            //console.log(a);
        });
    }

    useEffect(() => {
        buscar(); // eslint-disable-next-line
    }, [id]);

    if (!dados) return null;

    return (
        <div className='container'>
            <Card title='Cadastro de Categoria'>
                <div className='row'>
                    <div className='col-lg-12'>
                        <div className='bs-component'>
                            <FormGroup label='Nome: *' htmlFor='inputNome'>
                                <input
                                    type='text'
                                    id='inputNome'
                                    value={nome}
                                    className='form-control'
                                    name='nome'
                                    onChange={(e) => setNome(e.target.value)}
                                />
                            </FormGroup>
                            <FormGroup label='Descricao: *' htmlFor='inputDescricao'>
                                <input
                                    type='text'
                                    id='inputDescricao'
                                    value={descricao}
                                    className='form-control'
                                    name='descricao'
                                    onChange={(e) => setDescricao(e.target.value)}
                                />
                            </FormGroup>
                            <FormGroup label='Categoria pai: ' htmlFor='inputCategoriaPai'>
                                <select
                                    //type='text'
                                    id='inputCategoriaPai'
                                    value={categoriaPai != null ? categoriaPai.id : 0}
                                    className='form-control'
                                    name='categoriaPai'
                                    onChange={(e) => setCategoriaPai(e.target.value)}
                                >
                                    <option value="null" key="0">Nenhuma</option>
                                    {listaCategoriasPai.map((cat) => (
                                        <option value={cat.id} key={cat.id}>{cat.nome}</option>
                                    ))}
                                </select>

                            </FormGroup>
                            <Stack spacing={1} padding={1} direction='row'>
                                <button
                                    onClick={salvar}
                                    type='button'
                                    className='btn btn-success'
                                >
                                    Salvar
                                </button>
                                <button
                                    onClick={inicializar}
                                    type='button'
                                    className='btn btn-danger'
                                >
                                    Cancelar
                                </button>
                            </Stack>
                        </div>
                    </div>
                </div>
            </Card>
        </div>
    );
}

export default CadastroPedidos;
